sect2.programlisting:
	open Plplot
        let simple_example () =
        let xs = Array.init 21 (fun xi -> float xi -. 10.0) in
        let ys = Array.map (fun x -> x**2.0) xs in
        plinit ();
        plenv (-10.0) 10.0 0.0 100.0 0 0;
        plline xs ys;
        plend ();
        ()

        let () = simple_example ()
